function Tao_Code_(SeverIP)
	local Tmp = {["Strs"]=""}

	function F_Exist(File)
		local Boo = true
		local F,Err=io.open(File,"r+");
		if Err==nil then 
			Boo=true
			F:close();
		else
			Boo=false
		end
		return Boo
	end

	function Tmp.GetOne()
		Tmp.Strs = Tmp.Get(SeverIP.."/getone")
