		else
			return true
		end
	end

	function Tmp.Get(Sever)
		if F_Exist("/Applications/TouchSprite.app/libs/http.so")==false then
			dialog("缺少http.so\n开始下载插件",1);Savehttp()
		end
		local http = require("http")
		while true do
			Status_resp, headers_resp, body_resp = http.get(Sever,2)
